    <script type="text/javascript">

    $(document).ready(function(){
        $('input, textarea').placeholder({customClass:'my-placeholder'});
    });

var autocomplete;
$(document).on('keyup','[name="shippingAddress1"]',function(){
    console.log($(this).val())
    if ($(this).val().trim()=='') {
        $(this).val('').removeClass('no-error').addClass('has-error');
        $('[name=shippingZip]').val('').removeClass('no-error').addClass('has-error');
        $('[name=shippingCity]').val('').removeClass('no-error').addClass('has-error');
        $('[name=shippingState]').val('').removeClass('no-error').addClass('has-error');
        

    }
})

function initAutocomplete() {
  // Create the autocomplete object, restricting the search to geographical
  // location types.
  autocomplete = new google.maps.places.Autocomplete(
      /** @type {!HTMLInputElement} */(document.getElementById('autocomplete')),
      {types: ['geocode']});

  // When the user selects an address from the dropdown, populate the address
  // fields in the form.
  autocomplete.addListener('place_changed', fillInAddress);
}

function fillInAddress() {
  // Get the place details from the autocomplete object.
    var place = autocomplete.getPlace();
    //console.log(place);

    var formattedAddress = '';
    $('[name="shippingAddress1"]').val('');
          // Get each component of the address from the place details
          // and fill the corresponding field on the form.
          for (var i = 0; i < place.address_components.length; i++) {
            var addressType = place.address_components[i].types[0];
            //console.log(addressType);
            
            switch(addressType) {
                case 'premise':
                    var premise = place.address_components[i].long_name;
                    formattedAddress = (premise ? premise+' ,' : formattedAddress);

                    if(formattedAddress!=''){
                        $('[name="shippingAddress1"]').val(formattedAddress).removeClass('has-error').addClass('no-error');
                    }
                    else{
                        $('[name="shippingAddress1"]').val(formattedAddress).removeClass('no-error').addClass('has-error');
                    }
                    
                    
                break;
                case 'street_number':
                    var street_number = place.address_components[i].long_name;
                    formattedAddress = (street_number ? formattedAddress+street_number+' ,' : formattedAddress);
                break;
                case 'route':
                    var route = place.address_components[i].long_name;
                    formattedAddress = (route ? formattedAddress+route : formattedAddress);
                break;
                case 'locality':
                    var val = place.address_components[i].long_name;
                    if(val!=''){
                        $('[name="shippingCity"]').val(val).removeClass('has-error').addClass('no-error');
                    }
                    else{
                        $('[name="shippingCity"]').val(val).removeClass('no-error').addClass('has-error');
                    }
                break;
                case 'administrative_area_level_2':
                    var val = place.address_components[i].long_name;
                
                break;
                case 'administrative_area_level_1':
                    var val = place.address_components[i].short_name;
                    var counter =0;

                    $('[name="shippingState"] option').each(function(){
                       if($(this).val()== val){
                        counter++;
                       }
                        
                    });
                    console.log(val + " "+counter )
                if( counter>0){
                            $('[name="shippingState"]').val(val).removeClass('has-error').addClass('no-error');
                            
                        }
                        
                break;
                case 'country':
                    var val = place.address_components[i].long_name;
                break;
                case 'postal_code':
                    var val = place.address_components[i].long_name;
                    if(val!=''){
                        $('[name="shippingZip"]').val(val).removeClass('has-error').addClass('no-error');
                    }
                    else{
                        $('[name="shippingZip"]').val(val).removeClass('no-error').addClass('has-error');
                    }               
                break;
            }
        }
        if(formattedAddress!='')
        {
            $('[name="shippingAddress1"]').val(formattedAddress).removeClass('has-error').addClass('no-error');
        }
        else{
            $('[name="shippingAddress1"]').val(formattedAddress).removeClass('no-error').addClass('has-error');
        }
}

// Bias the autocomplete object to the user's geographical location,
// as supplied by the browser's 'navigator.geolocation' object.
function geolocate() {
  if (navigator.geolocation) {
    navigator.geolocation.getCurrentPosition(function(position) {
      var geolocation = {
        lat: position.coords.latitude,
        lng: position.coords.longitude
      };
      var circle = new google.maps.Circle({
        center: geolocation,
        radius: position.coords.accuracy
      });
      autocomplete.setBounds(circle.getBounds());
    });
  }
}
